user.parseFn:
(str) => {
  if (/^@[a-z]{3}\d{5}$/.test(str)) {
    return { uid: str.substring(1) };
  }
  if (/^[A-Z][A-Za-z ]{3,}$/.test(str)) {
    return { fullname: str, uid: str.split(/\s+/).map(s => s[0]).join('') };
  }
  return null;
};

user.lookupFn:
(str, raw) => {
  if (raw.uid != null && /^@[a-z]{1,3}/.test(str))
    return raw.uid.indexOf(str.substring(1)) === 0 ? 10 : 0;
  if (raw.uid != null && raw.uid.indexOf(str) > -1)
    return 1;
  return 0;
};

